mapper.select:
	select board_id as boardId, board_writer_Id as boardWriterId,
	board_title as boardTitle, board_content as boardContent,
	case when date_format(created_time, '%Y%m%d') = date_format(now(),
	'%Y%m%d')
	then date_format(created_time, '%H:%i:%s')
	else date_format(created_time, '%y.%m.%d')
	end as createdTime 
	from board


			where board_title like concat('%', #{word}, '%') and board_type =
			#{boardType}


			where ${key} = #{word} and board_type = #{boardType}




      where board_type = #{boardType}


	order by board_id desc
	limit #{start}, #{spp}

select count(board_id) from board
where board_title like concat('%', #{word}, '%')
where ${key} = #{word}

mapper.update:
update board
set
board_title = #{boardTitle}, board_content = #{boardContent}

	, board_attraction_info_id=#{boardAttractionInfoId}

where
board_id = #{boardId}
